import { Component, OnInit } from '@angular/core';
import { RazorpayService } from '../razorpay.service';
import { HttpClient } from '@angular/common/http';
import { ActivatedRoute, Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { FoodBoxService } from '../food-box.service';
import { CartItem } from '../model-classes/cart-item.model';
import { User } from '../model-classes/user.model';
import { OrderDetails } from '../model-classes/order-details.model';

declare var Razorpay: any;

@Component({
  selector: 'app-payment',
  templateUrl: './payment.component.html',
  styleUrls: ['./payment.component.css']
})
export class PaymentComponent implements OnInit {
  isLoading: boolean = false;
  userId!: number;
  amount!: number;
  email!: string;
  contact!: string;
  name!: string;
  address!: string;
  city!: string;
  state!: string;
  zip!: string;
  totalAmount: string = '5';
  distance!: number;
  cartItems: CartItem[] = [];
  paymentForm: FormGroup;
  emailInvalid: boolean = false;
  contactInvalid: boolean = false;
  totalCartCost: number = 0;
  alertMessage = '';
  user: User;
  order: OrderDetails;
  paymentIdForCod: string;
  message = '';
  alertmsg = false;
  regForm = this.builder.group({
    amount: this.builder.control({ value: this.amount, disabled: true }, Validators.required),
    email: this.builder.control({ value: this.email, disabled: true }, [Validators.required, Validators.email]),
    contact: this.builder.control({ value: this.contact, disabled: true }, Validators.required),
    name: this.builder.control({ value: this.name, disabled: true }, Validators.required),
    address: this.builder.control('', Validators.required),
    city: this.builder.control('', Validators.required),
    state: this.builder.control('', Validators.required),
    zip: this.builder.control('', Validators.required),
    distance: this.builder.control('', Validators.required)
  });
  constructor(
    private razorpayService: RazorpayService,
    private http: HttpClient,
    private service: FoodBoxService,
    private router: Router,
    private route: ActivatedRoute,
    private builder: FormBuilder
  ) {
    this.paymentForm = this.builder.group({
      amount: [{ value: '', disabled: true }, Validators.required],
      email: ['', [Validators.required, Validators.email]],
      contact: ['', [Validators.required, Validators.pattern(/^\d{10}$/)]],
      name: ['', Validators.required],
      address: ['', Validators.required],
      city: ['', Validators.required],
      state: ['', Validators.required],
      zip: ['', Validators.required],
      distance: ['', Validators.required],
    });
  }


  ngOnInit(): void {
    this.razorpayService.loadScript().subscribe(() => {
      // Razorpay SDK loaded
    });
    this.email = this.route.snapshot.params[`email`];
    this.getUser();
    this.totalAmount = '5'; // Assigning the value 5 to totalAmount
  }
  getUser(): void {
    this.service.getUserByEmail("'" + this.email + "'").subscribe(
      user => {
        this.user = user;
        this.email = user.email;
        this.userId = user.userId;
        this.contact = user.mobileNumber;
        this.name = user.name;
        this.getCartItems();
      }
    )
  }

  getCartItems(): void {
    this.service.getCartItems(this.userId).subscribe(cartItems => {
      this.cartItems = cartItems;
      this.amount = this.cartItemsTotalCost();
      this.totalAmount = this.calculateTotalAmount();
    });
  }

  calculateTotalAmount(): string {
    const baseAmount = this.amount;
    const distanceCharge = this.distance > 3 ? (this.distance - 3) * 30 : 0;
    return (baseAmount + distanceCharge).toLocaleString('en-IN', { style: 'currency', currency: 'INR' });
  }



  cartItemsTotalCost(): number {
    const distanceCharge = this.distance > 3 ? (this.distance - 3) * 30 : 0;
    return this.cartItems.reduce((totalAmount, cartItem) => totalAmount + distanceCharge + cartItem.totalFoodItemCost, 0);
  }





  makePayment() {
    this.alertmsg = true;
    const userId = this.userId;
    let paymentId = '';
    const paymentType = 'online';
    if (this.regForm.invalid) {
      console.log('Form is invalid. Cannot make payment.');
      this.message = 'Form is invalid. Cannot make payment.'
      return;
    }
    const options: any = {
      key: 'rzp_test_t51Ev8vhVfMTFO',
      amount: this.amount * 100, // Amount in paisa
      name: 'FoodBox',
      description: 'Payment for your order',
      handler: (response: any) => {
        console.log(response);
        paymentId = response.razorpay_payment_id;
        const orderDetails = {
          userId: userId,
          name: this.name,
          email: this.email,
          contact: this.contact,
          amount: this.amount,
          address: this.address,
          city: this.city,
          totalAmount: '5',
          zip: this.zip,
          distance: this.distance,
          paymentMethod: paymentType,
          paymentId: response.razorpay_payment_id,
          orderDate: new Date()
        };

        this.service.createOrder(orderDetails).subscribe(
          (data: any) => {
            console.log('Order created:', data);
            this.service.deleteCart(userId).subscribe();
            this.router.navigate(['/order-summary', data.orderId]);
          },
          (error: any) => {
            console.error('Error creating order:', error);
          }
        );
        this.service.getOrderDetailsByPaymentId(paymentId).subscribe(
          (orderDetails) => this.order = orderDetails
        )

      },
      prefill: {
        email: this.email,
        contact: this.contact,
        name: this.name
      },
      notes: {
        address: 'Your Address'
      },
      theme: {
        color: '#6466e3'
      },
      modal: {
        ondismiss: () => {
          console.log('dismissed');
        }
      }
    };

    const rzp = new Razorpay(options);
    rzp.on('payment.failed', (response: any) => {
      console.log('Payment failed:', response.error.code, response.error.description);
    });
    rzp.open();

    // Reset the amount after payment is made
    rzp.on('payment.success', (response: any) => {
      this.amount = 0; // Reset the amount to null
    });

  }

  payWithCash() {
    // Handle cash payment logic here
    this.alertmsg = true;
    this.isLoading = true; // Show the spinner

    this.service.getPaymentIdForCod().subscribe((id) => {
      this.paymentIdForCod = id;
      console.log(id);

    })
    if (this.regForm.invalid) {
      console.log('Form is invalid. Cannot make payment.');
      this.message = 'Form is invalid. Cannot make payment.'
      return;
    }
    setTimeout(() => {
      const userId = this.userId;
      const paymentType = 'cash';
      const totalAmountWithDistance = (Number(this.totalAmount) + this.distance).toString(); // Calculate totalAmount + distance
      const orderDetails = {
        userId: userId,
        name: this.name,
        email: this.email,
        contact: this.contact,
        amount: this.amount,
        address: this.address,
        city: this.city,
        state: this.state,
        distance: totalAmountWithDistance,
        zip: this.zip,
        totalAmount: this.totalAmount,
        paymentId: this.paymentIdForCod,
        paymentMethod: paymentType,
        orderDate: new Date()
      };

      this.service.createOrder(orderDetails).subscribe(
        (data: any) => {
          console.log('Order created:', data);
          this.service.deleteCart(userId).subscribe();
          this.router.navigate(['/order-summary', data.orderId]);
        },
        (error: any) => {
          console.error('Error creating order:', error);
        }
      );

      // Reset isLoading state after navigation
      setTimeout(() => {
        this.isLoading = false;
      }, 100); // A small delay to ensure smooth transition
    }, 100); // Wait for 0.1 seconds
  }

  validateEmail(email: string): void {
    this.emailInvalid = !/^[\w-]+(\.[\w-]+)*@([\w-]+\.)+[a-zA-Z]{2,7}$/.test(email);
  }

  validateContact(contact: string): void {
    this.contactInvalid = !/^\d{10}$/.test(contact);
  }

  get emailField() {
    return this.regForm.get('email');
  }

  get contactField() {
    return this.regForm.get('contact');
  }

  get nameField() {
    return this.regForm.get('name');
  }

  get addressField() {
    return this.regForm.get('address');
  }

  get cityField() {
    return this.regForm.get('city');
  }

  get stateField() {
    return this.regForm.get('state');
  }

  get zipField() {
    return this.regForm.get('zip');
  }
  get distanceField() {
    return this.regForm.get('distance');
  }
  get totalAmountField() {
    return this.regForm.get('totalAmount');
  }
}

<div
  class="container-fluid text-dark "
  style="
    background-image: url('assets/images/payment_page_bg.jpg');
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
    width: 100%;
    margin-top: -15px;
    margin-bottom: -8rem;
  "
>

<div class="row mt-3 mx-3" style="margin-top:25px ;">

  <div class="col-md-9 mt-5 mb-5 justify-content-center bg-warning ">
    <div class="card card-custom pb-4 bg-dark text-light">
      <div class="card-body mt-0 mx-5">
        <div class="text-center mb-3 pb-2 mt-3 ">
          <div class="text-container">
            <h1>Delivery Details</h1>
          </div>
        </div>
        <div class=" mb-3 pb-2 mt-3 ">
          <h5><strong>Customer Name:</strong> <span class="text text-warning">{{name}}</span></h5>
        </div>
        <form [formGroup]="regForm"class="mb-5">

          <div class="row mb-4">
            <label for="email" class="form-label">Email:</label>
            <div class="col">
              <input
              type="email"
              id="email"
              class="form-control text-secondary alert"
              name="email"
              [value]="email"
              disabled
              required

            />
          </div>
            </div>
            <div class="col">

                <label for="contact" class="form-label">Contact:</label>
                <input
                  type="text"
                  id="contact"
                  class="form-control text-secondary alert"
                  name="contact"
                  required
                  [value]="contact"
                  disabled
                 />

                <!-- [(ngModel)]="contact" -->       

            </div>

          <div class="row mb-4">
            <div class="col">
              <div class="form-outline">
                <label for="address" class="form-label is-invalid"
                >Address:</label
                >
                <textarea
                  id="address"
                  class="form-control"
                  formControlName="address"
                  name="address"
                  rows="3"
                  [(ngModel)]="address"
                  required
                ></textarea
                ><!-- [(ngModel)]="address" -->
                <div
                  class="alert alert-danger"
                  *ngIf="
                    regForm.controls.address.touched &&
                    regForm.controls.address.invalid
                  "
                >
                  Address should not be blank or invalid
                </div>
              </div>
            </div>
            <div class="col">
              <div class="form-outline">
                <label for="city" class="form-label">City:</label>
                <input
                  type="text"
                  id="city"
                  formControlName="city"
                  class="form-control"
                  name="city"
                  required
                  [(ngModel)]="city"
                /><!--  [(ngModel)]="city" -->
                <div
                  class="alert alert-danger"
                  *ngIf="
                    regForm.controls.city.touched &&
                    regForm.controls.city.invalid
                  "
                >
                  City should not be blank or invalid
                </div>
              </div>
            </div>
          </div>
          <div class="row mb-4">
            <div class="col">
              <div class="form-outline">
                <label for="state" class="form-label">State:</label>
                <input
                  type="text"
                  id="state"
                  formControlName="state"
                  class="form-control"
                  name="state"
                  [(ngModel)]="state"
                  required
                /><!-- [(ngModel)]="state"   -->
                <div
                  class="alert alert-danger"
                  *ngIf="
                    regForm.controls.state.touched &&
                    regForm.controls.state.invalid
                  "
                >
                  State should not be blank or invalid
                </div>
              </div>
            </div>
            <div class="col">
              <div class="form-outline">
                <label for="zip" class="form-label">ZIP Code:</label>
                <input
                  type="text"
                  id="zip"
                  formControlName="zip"
                  class="form-control"
                  name="zip"
                  [(ngModel)]="zip"
                  required
                /><!-- [(ngModel)]="zip" -->
                <div
                  class="alert alert-danger"
                  *ngIf="
                    regForm.controls.zip.touched && regForm.controls.zip.invalid
                  "
                >
                  Zip should not be blank or invalid
                </div>
              </div>
            </div>
            <div class="col">
              <div class="form-outline">
                <label for="distance" class="form-label">Distance:</label>
                <input
                  type="number"
                  id="distance"
                  formControlName="distance"
                  class="form-control"
                  name="distance"
                  [(ngModel)]="distance"
                  required
                /><!-- [(ngModel)]="zip" -->
                <div
                  class="alert alert-danger"
                  *ngIf="
                    regForm.controls.distance.touched && regForm.controls.distance.invalid
                  "
                >
                  distance should not be blank or invalid
                </div>
              </div>
            </div>
          </div>

          <div class="float-end ">
            <!-- Submit button -->

             <button
             type="submit"
             class="btn btn-primary btn-rounded py-2 ml-2 px-4 mb-5"
             style="margin-left: 10px"
             (click)="makePayment()"
           >
             Pay
           </button>
           <button
             type="button"
             class="btn btn-secondary btn-rounded py-2 px-4 mb-5"
             style="margin-right: 10px"
             (click)="payWithCash()"
           >
             Pay with Cash 
           </button>
           <div *ngIf="alertmsg" class="alert alert-danger">{{message }}</div>
          </div>

        </form>
      </div>
    </div>
  </div>
  <div class="col-md-3 mt-5 mb-5 ">
    <div class="card mb-4">
      <div class="card-header bg-dark text-light py-3">

        <h5 class="mb-0">Summary</h5>
      </div>
      <div *ngIf="cartItems.length > 0"></div>
      <div class="card-body">
        <ul class="list-group list-group-flush">
          <!-- <li  class="list-group-item"> -->
          <li *ngFor="let cartItem of cartItems" class="list-group-item d-flex justify-content-between align-items-center border-0 px-0 pb-0">
            {{ cartItem.foodItem.name }}-
            {{ cartItem.quantity }} pcs
            <span>{{ cartItem.totalFoodItemCost | currency : "INR" }}</span>
          </li>
          <hr>
          <li class=" list-group-item d-flex justify-content-between align-items-center border-0 px-0 mb-3 ">
            <div>
              <strong>Total amount</strong>
              <strong>
                <p class="mb-0">(including VAT)</p>
              </strong>
            </div>
            <span><strong>{{ amount | currency : "INR" }}</strong></span>
          </li>

        </ul>

        <!-- <button type="button" class="btn btn-primary btn-lg btn-block">
          Make purchase
        </button> -->
      </div>
    </div>
  </div>
</div>
</div>
