import { Component, ElementRef, Renderer2, ViewChild } from '@angular/core';
import { FoodBoxService } from '../food-box.service';
import { AbstractControl, FormBuilder, FormControl, Validators } from '@angular/forms';
import { Router } from '@angular/router';
function passwordValidator(control: AbstractControl): { [key: string]: boolean } | null {
  const value = control.value;
  const hasUpper = /[A-Z]/.test(value);
  const hasLower = /[a-z]/.test(value);
  const hasNumber = /\d/.test(value);
  const hasSpecial = /[!@#$%^&*()_+\-=[\]{};':"\\|,.<>/?]+/.test(value);

  if (hasUpper && hasLower && hasNumber && hasSpecial) {
    return null;
  } else {
    return { 'invalidPassword': true };
  }
}
@Component({
  selector: 'app-user-signin-signup',
  templateUrl: './user-signin-signup.component.html',
  styleUrls: ['./user-signin-signup.component.css']
})
export class UserSigninSignupComponent {
  successMessage = '';
  errorMessage = '';
  constructor(private service: FoodBoxService, private fb: FormBuilder, private renderer: Renderer2, private router: Router) { }
  login = this.fb.group({

    'email': new FormControl('', [Validators.required]),
    'password': new FormControl('', [Validators.required]),
  });

  addUser = this.fb.group({
    'name': new FormControl('', [Validators.required, Validators.pattern(/^[a-zA-Z ]*$/)]),
    'password': new FormControl('', [Validators.required, Validators.minLength(6), passwordValidator]),
    'mobileNumber': new FormControl('', [Validators.required, Validators.pattern('[0-9]{10}')]),
    'email': new FormControl('', [Validators.required, Validators.email]),
  });

  message: any;
  email: any;
  public userLogin() {
    let response = this.service.loginUser(this.login.value);
    response.subscribe((data: any) => {
      this.message = data;

      if (this.message !== "invalid") {
        sessionStorage.setItem('userSession', JSON.stringify(this.message));

      } else {
        this.errorMessage = 'Password or Email is incorrect!';
      }
      this.router.navigate(['/user-dashboard'])
    });
  }
  public registerUser() {

    this.service.registerUser(this.addUser.value).subscribe(
      response => {
        if (response == 'Registered Successfully! Login to continue') {
          this.successMessage = response;
          this.errorMessage = '';
        }
        else {
          this.successMessage = '';
          this.errorMessage = response;
        }
      }
    );
  }
  @ViewChild('signinForm') signinForm: ElementRef;
  @ViewChild('signupForm') signupForm: ElementRef;
  @ViewChild('toSigninButton') toSigninButton: ElementRef;
  @ViewChild('toSignupButton') toSignupButton: ElementRef;

  showSignInForm() {
    this.successMessage = '';
    this.errorMessage = '';
    this.renderer.addClass(this.toSigninButton.nativeElement, 'top-active-button');
    this.renderer.removeClass(this.toSignupButton.nativeElement, 'top-active-button');

    this.renderer.setStyle(this.signupForm.nativeElement, 'display', 'none');
    this.renderer.setStyle(this.signinForm.nativeElement, 'display', 'block');
  }

  showSignUpForm() {
    this.successMessage = '';
    this.errorMessage = '';
    this.renderer.addClass(this.toSignupButton.nativeElement, 'top-active-button');
    this.renderer.removeClass(this.toSigninButton.nativeElement, 'top-active-button');

    this.renderer.setStyle(this.signinForm.nativeElement, 'display', 'none');
    this.renderer.setStyle(this.signupForm.nativeElement, 'display', 'block');
  }


}
<h2 style="text-align: center; color: green">{{ successMessage }}</h2>
<h2 style="text-align: center; color: red">{{ errorMessage }}</h2>
<div class="main-box">
  <div class="form-cont">
    <div class="top-buttons">
      <button #toSignupButton class="to-signup" (click)="showSignUpForm()">
        Sign Up
      </button>
      <button
        #toSigninButton
        class="to-signin top-active-button"
        (click)="showSignInForm()"
      >
        Log In
      </button>
    </div>

    <!-- Login Form -->
    <div #signinForm class="form form-signin">
      <form [formGroup]="login">
        <div class="input-container">
         <input type="text" formControlName="email" name="" required="">
         <label>E-MAIL</label>
        </div>
        <div
          *ngIf="login.get('email').errors && login.get('email').touched"
          class="alert alert-danger"
        >
          * Email is required
        </div>
        <div class="input-container">
         <input type="password" formControlName="password" name="" required="">
         <label>PASSWORD</label>
        </div>
        <div
          *ngIf="login.get('password').errors && login.get('password').touched"
          class="alert alert-danger"
        >
          * Password is required
        </div>
        <input
          type="button"
          (click)="userLogin()"
          class="form-btn"
          value="Log In"
          [disabled]="!login.valid"
        />
        <br /><br />
        <a (click)="showSignUpForm()" class="lined-link to-signup-link"
          >Create new account</a
        >
      </form>
    </div>

    <!-- Sign Up Form -->
    <div #signupForm class="form form-signup">
      <form [formGroup]="addUser">
        <div class="input-containerr">
          <input type="text" formControlName="name" name="" required="">
          <label>FULL NAME</label>
        </div>
        <div
          *ngIf="addUser.controls.name.invalid && addUser.controls.name.touched"
          class="alert alert-danger"
        >
          * Please enter a valid name.
        </div>

        <div class="input-containerr">
          <input type="text" formControlName="email" name="" required="" >
          <label>E-MAIL</label>
        </div>
        <div
          *ngIf="
            addUser.controls.email.invalid && addUser.controls.email.touched
          "
          class="alert alert-danger"
        >
          * Email is required.
        </div>
        <div class="alert alert-danger" *ngIf="addUser.get('email')?.errors?.['email']">
          * Invalid email format
        </div>
        <div class="input-containerr">
          <input type="text" formControlName="mobileNumber" name="" required="">
         <label>Phone Number:</label>
        </div>
        <div
          *ngIf="
            addUser.controls.mobileNumber.invalid &&
            addUser.controls.mobileNumber.touched
          "
          class="alert alert-danger"
        >
          * Mobile Number is required.
        </div>
        <div class="alert alert-danger" *ngIf="addUser.get('mobileNumber')?.errors?.['mobileNumber']">
          * Mobile Number should contain only numbers and 10 digit long.
        </div>
        <div class="input-containerr">
          <input type="password" formControlName="password" name="" required="">
          <label>CREATE PASSWORD</label>
         </div>

        <div
          *ngIf="
            addUser.controls.password.invalid &&
            addUser.controls.password.touched
          "
          class="alert alert-danger"
        >
          * Password is required.
        </div>
        <div class="alert alert-danger" *ngIf="addUser.get('password')?.errors?.['minlength']">
          * Password should be at least 6 characters long
        </div>
        <div class="alert alert-danger" *ngIf="addUser.get('password').touched && addUser.get('password').invalid">
          * Password should contain at least 1 upper case, 1 lower case, 1 number, and 1 special character
        </div>
        <input
          type="button"
          (click)="registerUser()"
          class="form-btn"
          value="Sign Up"
          [disabled]="!addUser.valid"
        />
        <br /><br />
        <a (click)="showSignInForm()" class="lined-link to-signin-link"
          >I'm already a member</a
        >
      </form>
    </div>
  </div>
  <div class="clear-fix"></div>
</div>
